import axios from "axios";
import { useState } from "react";
import ImgCard from "./ImgCard";

const ImageSearch = () =>{

    const [imgSearch, setImgSearch] = useState("");
    const [imgData,setImgData] = useState([]);
    const [spinner,setSpinner] = useState([false]);

    const loadData = async (e)=>{
        try
        {
            let apiData;
            apiData= await axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=c54d9e46b9413b065cbce86c8e416681&language=en-US&page`);
            console.log(apiData.data.results);
            let spinnerData = apiData.data.results.map(()=> {return true});
            setSpinner([...spinnerData]);
            console.log(spinner);
            setImgData(apiData.data.results.length>0?apiData.data.results:[]);
        }
        catch(e){
            console.log("Image Load Failed : "+e);
        }
    };

    useEffec(()=>{loadData()},[]);

    return <section className="container my-4">
        <div className="card mx-auto">
            <h1 className="card-header text-center">Image Search</h1>
            <div className="card-body">
                <input type="text" className="form-control w-50 mx-auto my-2" placeholder="Search Image Here" value={imgSearch}/>
            </div>
        </div>
        <div className="d-flex flex-wrap gap-1 justify-content-center">
            {imgData.map((value,i)=>{ return <ImgCard key={i} id={i}  img={`https://image.tmdb.org/t/p/originals${value.backdrop_path}`} alt={value.title} setSpinner={setSpinner} spinner={spinner} />})}
        </div>
    </section>;
}

export default ImageSearch;
